"use strict";angular.module("angular-geo-maplocation",["google-maps"]).directive("geoMaplocation",function(){return{restrict:"E",scope:{start:"=",coords:"=",zoom:"=?",tooltip:"=?"},template:'<google-map center="map.start" zoom="map.zoom" draggable="true" options="map.options"><marker coords="coords" options="marker.options" events="marker.events"></marker></google-map>',controller:["$scope",function(a){if(!a.coords)throw new Error("coords must be supplied");a.map={zoom:a.zoom||8,options:{streetViewControl:!1},start:a.start},a.marker={options:{draggable:!0,title:a.tooltip||"Drag marker to geo-location"},events:{dragend:function(b){a.$apply(function(){a.coords.latitude=b.getPosition().lat(),a.coords.longitude=b.getPosition().lng()})}}}}]}});